import React, {useState} from 'react'

export default function TextForm(props) {
    const handleUpClick = ()=>{
        let newText = Text.toUpperCase();
        setText(newText);
    }
    const handleLowClick = ()=>{
        let newText = Text.toLowerCase();
        setText(newText);
    }
    const handleOnChange = (event)=>{
       console.log("value is changed nopw");
       setText(event.target.value);
    }
    const [Text, setText] = useState("Enter text here");
  return (
    <>
    <div>
        <h1>{props.title}</h1>
        <div className="mb-3">
            <textarea className="form-control" onChange={handleOnChange} value={Text} id="myBox" rows="8"></textarea>
        </div>
        <button className='btn btn-primary mx-2' onClick={handleUpClick}>Convert To Uppercase</button>
        <button className='btn btn-secondary mx-1' onClick={handleLowClick}>Convert To Lowercase</button>
    </div>
    <div className="container">
        <h1>Total no. of words {Text.split(" ").length}</h1>
        <h1>Total no. of characters {Text.length}</h1>
    </div>
    </>
  )
}
